elements.expression:
filters
| essql 
  query="SELECT startTimeInMillis, DATETIME_FORMAT(build_start_time,'yyyy-MM-dd HH:mm:ss.S') build_start_time,
DATETIME_FORMAT(build_end_time,'yyyy-MM-dd HH:mm:ss.S') build_end_time, BUILD_TAG, build_duration_seconds, message FROM \"created*\"
where build_end_time is not null
order by startTimeInMillis desc"
| head
| markdown 
  "## Jenkins Build Job Data - HTTP
#### From Call To HTTP Input on Logstash
{{#each rows}}
 ### Build Tag: {{BUILD_TAG}} 
 **Build Start Time: {{build_start_time}}**

 **Build End Time:   {{build_end_time}}**

 **Build Duration:   {{build_duration_seconds}}**

 **Build Message:   {{message}}**
{{/each}}
"
| render 
  css=".canvasMarkdown {
border-color: black
}
.canvasMarkdown h2 {
font-size: 16px !important;
font-weight: bold;
color: #000316 !important;
}
.canvasMarkdown h3 {
font-size: 14px !important;
font-weight: bold;
color: black !important;
}
.canvasMarkdown h4 {
font-size: 12px !important;
font-weight: bold;
color: black !important;
padding-left: 10px;
}
.canvasMarkdown p {
font-size: 12px !important;
font-weight: bold;
color: black !important;
padding-left: 10px;
}
" 
  containerStyle={containerStyle border="3px solid " borderRadius="6px" padding="6px" backgroundColor="#FFFFFF"}

